    password2: ""
  });

  function handleChange(event) {
    const { value, name } = event.target;
    setFullName((previousValue) => {
      if (name === "fName") {
        return {
          fName: value,
          lName: previousValue.lName
        };
      } else if (name === "lName") {
        return {
          fName: previousValue.fName,
          lName: value
