import React, {useState, useEffect} from "react";

function TimerTillNovember(props) {
  const [timerText, setTimerText] = useState(new Date().toString())

  useEffect(() => {
    const timer = setTimeout(() => {

      const total = Date.parse('Nov 1 2020') - Date.parse(new Date());
      const seconds = Math.floor( (total/1000) % 60 );
      const minutes = Math.floor( (total/1000/60) % 60 );
      const hours = Math.floor( (total/(1000*60*60)) % 24 );
      const days = Math.floor( total/(1000*60*60*24) );
      setTimerText(days + ":" + hours + ":" + minutes + ":" + seconds);
    }, 1000);
  });


  switch (props.size) {
    case 1:
      return <h1 class={props.class}>{timerText}</h1>;
    case 2:
      return <h2 class={props.class}> 21: 6: 23: 53 </h2>;
    case 3:
      return <h3 class={props.class}> 21: 6: 23: 53 </h3>;
    case 4:
      return <h4 class={props.class}> 21: 6: 23: 53 </h4>;
    case 5:
      return <h5 class={props.class}>{timerText}</h5>;
    case 6:
      return <h6 class={props.class}> 21: 6: 23: 53 </h6>;
    default:
      return 'foo';
  }
}

export default TimerTillNovember;
