import React, {useState} from "react";
import axios from "axios";
import Querystring from "query-string"

function Register(props) {
  if (props.auth){
    props.history.push('/')
  }

  const [result, setResult] = useState(null);
  const [success, setSuccess] = useState(false);

  const [newUser, setNewUser] = useState({
    username: "",
    email: "",
    password: "",
    password2: ""
  });

  function handleChange(event) {
    const { value, name } = event.target;
    setNewUser((prevValue) => {
      return {
        ...prevValue,
        [name]: value
      };
    });
  }

  function getGreetingText(){
    if (newUser.username){
      if (newUser.username.length > 12){
        return "Hello " + newUser.username.substring(0, 12) + "..."
      }
      return "Hello " + newUser.username
    }
    return "Hello";
  }

  function submit(event){
    event.preventDefault();
    axios({
      method: 'post',
      data: Querystring.stringify(newUser),
      withCredentials: true,
      url: 'http://localhost:5000/register',
      headers: {
        'Content-type': 'application/x-www-form-urlencoded'
      }
    }).then(res => {
      console.log(res)
      if (res.data == "success"){
        props.reAuth();
        setSuccess(true);


      }else{
        setResult((prevValue) => {
          return (
            res.data.toString()
          )
        });
      }
    })
    .catch(function(err){
      setResult((prevValue) => {
        return (
          "Nuts! An unknown error occured"
        )
      });
    })
  }

  return (
    <div className="center">
      <div class="container d-flex h-100 align-items-center">
        <div class="mx-auto text-center">
          <h1>
            {getGreetingText()}
          </h1>
          <form>
            <input
              class="input"
              value={newUser.email}
              onChange={handleChange}
              name="email"
              placeholder="Email"
              type="Username"
            />
            <input
              class="input"
              value={newUser.username}
              onChange={handleChange}
              placeholder="Username"
              type="text"
              field="username"
            />
            <input
              class="input"
              value={newUser.password}
              onChange={handleChange}
              name="password"
              placeholder="Password"
              type="password"
              field="password"
            />
            <input
              class="input"
              value={newUser.password2}
              onChange={handleChange}
              name="password2"
              placeholder="Confirm Password"
              type="password"
              field="password2"
            />
            <button type="submit" onClick={submit} class="form-button btn btn-danger join-button">Sign Up</button>
            {result && <div class="invalid-feedback d-block">{result}</div>}
            {success && <div class="valid-feedback d-block">Success! Please verify your email address</div>}
          </form>
        </div>
      </div>
    </div>
  );
}

export default Register;
